function enableUpload(){var b=document.getElementById("uploadInput");if(b.value!=""){document.getElementById("uploadDiv").style.display="none";document.getElementById("selectedFileDiv").style.display="";var a=document.getElementById("filename");while(a.firstChild){a.removeChild(a.firstChild)}a.appendChild(document.createTextNode(b.value))}}function selectNewFile(){document.getElementById("uploadDiv").style.display="";document.getElementById("selectedFileDiv").style.display="none";document.getElementById("uploadInput").value=""};